import React, { useRef } from 'react'
import { Box,  Button,  Card, CardActionArea, CardActions, CardContent, CardMedia, Popover, Typography } from '@mui/material'
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";


export const GroundMapp = () => {
    // Popover on Hover
    const [anchorEl, setAnchorEl] = React.useState<SVGGElement | null>(null)
    const open = Boolean(anchorEl);
    const handlePopoverOpen = (event: React.MouseEvent<SVGGElement>) => {
        setAnchorEl(event.currentTarget);
    }
    const handlePopoverClose = () => {
        setAnchorEl(null);
    }

    // Popover on Click
    const [anchorClick, setAnchorClick] = React.useState<SVGGElement | null>(null);
    const handleClick = (event: React.MouseEvent<SVGGElement>) => {
        setAnchorClick(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorClick(null);
    };
    const openEl = Boolean(anchorClick);
    const id = openEl ? 'simple-popover' : undefined;

    // Pan Zoom

  return (
    <div className='border border-red-100 relative w-auto h-full max-w-none max-h-none'>
        <TransformWrapper
        initialScale={1}
        initialPositionX={200}
        initialPositionY={100}
      >
        {({ zoomIn, zoomOut, resetTransform, ...rest }) => (
          <React.Fragment>
            <div className="tools">
              <button onClick={() => zoomIn()}>+</button>
              <button onClick={() => zoomOut()}>-</button>
              <button onClick={() => resetTransform()}>x</button>
            </div>
            <TransformComponent>
                <img src="./mapp.svg" alt="test" />
              <div>Example text</div>
            </TransformComponent>
          </React.Fragment>
        )}
      </TransformWrapper>

        {/* Popover on Hover Element */}
        <Popover id="mouse-over-popover" sx={{ pointerEvents: 'none', }} open={open} anchorEl={anchorEl} anchorOrigin={{ vertical: 'top', horizontal: 'center', }} transformOrigin={{ vertical: 'bottom', horizontal: 'center', }} onClose={handlePopoverClose} disableRestoreFocus>
            <Card className='w-32 h-32 p-2'>
                <CardActionArea>
                    <CardMedia component="img" height="140" image="https://source.unsplash.com/random/128x128" alt="green iguana"/>
                </CardActionArea>
            </Card>
        </Popover>

        {/* Popover on Click Element */}
        <Popover id={id} open={openEl} anchorEl={anchorClick} onClose={handleClose} anchorOrigin={{ vertical: 'top', horizontal: 'center', }} transformOrigin={{ vertical: 'bottom', horizontal: 'center', }}>
            <CardContent className='w-80'>
                <Typography sx={{ textTransform: 'uppercase', fontStyle: 'bold', fontSize: '16',}}>
                    Nama Tenant
                </Typography>
            </CardContent>
            <CardActions className='float-right'>
                <Button sx={{ textTransform: 'none' }} variant="contained" size="small" disableElevation>Lihat Lokasi</Button>
            </CardActions>
        </Popover>
    </div>
  )
}
